using System.Collections;using System.Collections.Generic;using UnityEngine;public class DestroyAfterTime : MonoBehaviour{    [SerializeField]    [Tooltip("Time after which object should be destroyed.")]    private float destroyTime = 1f;    private WaitForSeconds destroyWait;    private void Start()    {        destroyWait = new WaitForSeconds(destroyTime);        StartCoroutine(DestroySelf());    }    private IEnumerator DestroySelf()    {        yield return destroyWait;        Destroy(gameObject);    }}