using System.Collections;using System.Collections.Generic;using UnityEngine;public class Egg : MonoBehaviour{    private Transform thisTransform;    private Rigidbody2D thisRb;    private void Start()    {        thisTransform = transform;        thisRb = GetComponent<Rigidbody2D>();    }    public void SetPosition(Vector3 position)    {        thisTransform.position = position;    }    public void StartFalling()    {        thisRb.isKinematic = false;    }    public float GetHeight()    {        return thisTransform.localScale.y;    }}