using System.Collections;using System.Collections.Generic;using UnityEngine;public class Egg : MonoBehaviour{    [SerializeField]    [Tooltip("Particle system that plays when the egg cracks.")]    private ParticleSystem crackParticles;    [SerializeField]    [Tooltip("Particle system that plays when the egg reaches the nest.")]    private ParticleSystem winParticles;    private Transform thisTransform;    private Rigidbody2D thisRb;    private Animator animator;    private void Start()    {        thisTransform = transform;        thisRb = GetComponent<Rigidbody2D>();        animator = GetComponent<Animator>();    }    public void SetPosition(Vector3 position)    {        thisTransform.position = position;    }    public void StartFalling()    {        thisRb.isKinematic = false;    }    public float GetHeight()    {        return thisTransform.localScale.y;    }    public void Crack()    {        crackParticles.Play();        animator.SetTrigger("EggCrack");    }    public void Win()    {        winParticles.Play();        LevelManager.instance.Win();    }    public void DestroyEgg()    {        Destroy(gameObject);    }}