using System.Collections;using System.Collections.Generic;using UnityEngine;public class CrackedEgg : MonoBehaviour{    [SerializeField]    [Tooltip("Force power for the random direction the shell shoots when spawned.")]    private float spawnThrustForce = 100f;    private Animator animator;    private Rigidbody2D thisRb;    private Transform thisTransform;    private void Awake()    {        thisRb = GetComponent<Rigidbody2D>();        thisTransform = transform;        animator = GetComponent<Animator>();        animator.SetTrigger("EggCrack");        AddRandomForce();    }    public void DestroyEgg()    {        Destroy(gameObject);    }    public void SetVelocity(Vector3 velocity)    {        thisRb.velocity = velocity;    }    public void Rotate(Vector3 rotation)    {        thisTransform.Rotate(rotation);    }    private void AddRandomForce()    {        thisRb.AddForce(Random.insideUnitCircle * spawnThrustForce);    }}