using System.Collections;using System.Collections.Generic;using UnityEngine;public class Egg : MonoBehaviour{    [SerializeField]    [Tooltip("Particle system that plays when the egg cracks.")]    private ParticleSystem crackParticles;    [SerializeField]    [Tooltip("Particle system that plays when the egg reaches the nest.")]    private ParticleSystem winParticles;    [SerializeField]    [Tooltip("Object used to animate the egg.")]    private GameObject animatorObject;    [SerializeField]    [Tooltip("Prefab representing the Top egg shells we create when egg is cracked")]    private GameObject eggCrackPrefabTop;    [SerializeField]    [Tooltip("Prefab representing the Bottom egg shells we create when egg is cracked")]    private GameObject eggCrackPrefabBottom;    [SerializeField]    [Tooltip("List of all the compound colliders for this egg.")]    private Collider2D[] colliders;    private Transform thisTransform;    private Rigidbody2D thisRb;    private DestroyAfterTime selfDestroy;    public EggCount eggCount;    private void Start()    {        thisTransform = transform;        thisRb = GetComponent<Rigidbody2D>();        colliders = GetComponents<Collider2D>();        selfDestroy = GetComponent<DestroyAfterTime>();    }    public void SetPosition(Vector3 position)    {        thisTransform.position = position;    }    public void StartFalling()    {        thisRb.isKinematic = false;    }    public float GetHeight()    {        return thisTransform.localScale.y;    }    public void Crack()    {        SpawnEggShells();        crackParticles.Play();        selfDestroy.enabled = true;        Hide();

        if (!eggCount.HasEggs())        {            eggCount.DisplayOutOfEggs();        }    }    public void Win()    {        winParticles.Play();        LevelManager.instance.Win();    }    public void DestroyEgg()    {        Destroy(gameObject);    }    private void SpawnEggShells()    {        SpawnAndMatchVelocity(eggCrackPrefabTop);        SpawnAndMatchVelocity(eggCrackPrefabBottom);    }    private CrackedEgg SpawnAndMatchVelocity(GameObject eggCrackPrefab)    {        GameObject crackedEggObject = Instantiate(eggCrackPrefab, thisTransform);        CrackedEgg crackedEgg = crackedEggObject.GetComponent<CrackedEgg>();        crackedEgg.SetVelocity(thisRb.velocity);        return crackedEgg;    }    public void Hide()    {        animatorObject.SetActive(false);        foreach(Collider2D collider in colliders)        {            collider.enabled = false;        }    }}