using UnityEngine;public class EggSpawnArea : MonoBehaviour{    [SerializeField]    [Tooltip("Prefab for the draggable and droppable egg.")]    private GameObject eggPrefab;    [SerializeField]    [Tooltip("Bar below which you cannot drop/drag the egg.")]    private Transform droppingBar;    [SerializeField]    [Tooltip("Offset from the center of the mouse where the dragged egg should appear.")]    private Vector3 dragOffset;    private bool isDragging = false;    private Egg draggedEgg;    private Egg eggPool;    private void Start()    {        PrePopulateEgg();    }    // small object pool for the egg    private void PrePopulateEgg()    {        eggPool = Instantiate(eggPrefab).GetComponent<Egg>();    }    public void OnMouseDown()    {        isDragging = true;        draggedEgg = eggPool;        PrePopulateEgg();        MoveEggToMouse(draggedEgg);    }    public void OnMouseDrag()    {        if (isDragging)        {            MoveEggToMouse(draggedEgg);        }    }    private void MoveEggToMouse(Egg egg)    {        Vector3 mousePosition = new Vector3(Input.mousePosition.x, Input.mousePosition.y, 0f);        Vector3 objPosition = Camera.main.ScreenToWorldPoint(mousePosition);        objPosition.z = 0f;        objPosition.y = Mathf.Max(objPosition.y, GetLowestY());        egg.SetPosition(objPosition + dragOffset);    }    // shouldn't be able to go below dropping bar    private float GetLowestY()    {        return droppingBar.position.y + draggedEgg.GetHeight() / 2f - dragOffset.y;    }    public void OnMouseUp()    {        isDragging = false;        draggedEgg.StartFalling();        draggedEgg = null;    }}