using UnityEngine;public class EggSpawnArea : MonoBehaviour{    [SerializeField]    [Tooltip("Prefab for the draggable and droppable egg.")]    private GameObject eggPrefab;    [SerializeField]    [Tooltip("Bar below which you cannot drop/drag the egg.")]    private Transform droppingBar;    private bool isDragging = false;    private Egg draggedEgg;    private void Start()    {    }    public void OnMouseDown()    {        isDragging = true;        draggedEgg = Instantiate(eggPrefab).GetComponent<Egg>();    }    public void OnMouseDrag()    {        if (isDragging)        {            Vector3 mousePosition = new Vector3(Input.mousePosition.x, Input.mousePosition.y, 0f);            Vector3 objPosition = Camera.main.ScreenToWorldPoint(mousePosition);            objPosition.z = 0f;            objPosition.y = Mathf.Max(objPosition.y, GetLowestY());            draggedEgg.SetPosition(objPosition);        }    }    // shouldn't be able to go below dropping bar    private float GetLowestY()    {        return droppingBar.position.y + draggedEgg.GetHeight() / 2f;    }    public void OnMouseUp()    {        isDragging = false;        draggedEgg.StartFalling();        draggedEgg = null;    }}