using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.SceneManagement;public class LevelManager : SingletonMonoBehaviour<LevelManager>{    [SerializeField]    [Tooltip("UI that shows up when player wins.")]    private GameObject winningPanel;    [SerializeField]    [Tooltip("UI that shows up when player pauses.")]    private GameObject menuPanel;    protected override void Awake()    {        if (!InitializeSingleton(this))        {            return;        }    }    public void Win()    {        winningPanel.SetActive(true);    }    public void RestartLevel()    {        SceneManager.LoadScene(SceneManager.GetActiveScene().name);    }    public void ToggleMenu()
    {
        menuPanel.SetActive(!menuPanel.activeSelf);
    }}